teams_data = json.loads(teams)
player_data = json.loads(players)

def cls():
    os.system("cls" if os.name == "nt" else "clear")

def getInfo(target, data, key):
    for each in data:
        if each[key].lower() == target.lower():
            return each
    return -1

def main():
    while True:
        featured = player_data[random.randint(0, len(player_data))]["name"]

        cls()
        print(info.format(featured))

        selection = input("player > ")
        playerInfo = getInfo(selection, player_data, "name")
        if playerInfo == -1:
            print("-----------------------")
            print("ERROR: Player was not found")
            print("-----------------------")
            input("Press Enter to continue")
            continue
        teamInfo = getInfo(playerInfo["team_id"], teams_data, "id")
        if teamInfo == -1:
            Print("Team not found, most likely error in database")
        else:
            cls()
            print(info.format(featured))
            print("player > " + selection)
            print("team   > " + teamInfo["name"])
            print("-----------------------")
            input("Press Enter to continue")

if __name__ == "__main__": main()
