info = """.: Peoples Database :.
---------------------
get  | Gets person by ID
scan | gets people by OCCUPATION
---------------------"""
getInfo = """---------------------
ID:         {}
Forename:   {}
Surname:    {}
Age:        {}
Occupation: {}
---------------------"""


data = [row.split(",") for row in db.split("\n")[1:]]

def cls():
    os.system("cls" if os.name == "nt" else "clear")


def main():
    while True:
        cls()

        print(info)

        command = input("> ").lower()

        if command == "scan":
            print("---------------------")
            occupation = input("OCCUPATION = ")
            people = [(row[0], row[1]) for row in data if row[4].lower() == occupation.lower()]
            print("---------------------")
            for person in people:
                print("{:<3} | {}".format(*person))
            print("---------------------")

        if command == "get":
            while True:
                print("---------------------")
                try:
                    id = int(input("ID = "))
                    print(getInfo.format(*tuple(data[id])))
                    break
                except ValueError:
                    print("invalid id")
                except IndexError:
                    print("Id doesn't exist")
        input("Press Enter to continue")

if __name__ == "__main__": main()
