# Remove first row
db = [ x.split(',') for x in db_raw.split('\n')[1:] ]

def cls():
    os.system("cls" if os.name == "nt" else "clear")

def get_id():
    id = input("ID > ")

    print('-'*25)
    try:
        entry = next(filter(lambda x: x[0] == id, db))
        print("ID = {0}\nFORENAME = {1}\nSURNAME = {2}\nAGE = {3}\nOCCUPATION = {4}".format(*entry))
    except StopIteration:
        sys.stderr.write("ERROR: Invalid id: '{}'\n".format(id))
    print('-'*25)

def scan():
    occupation = input("OCCUPATION > ")

    print('-'*25)
    for match in filter(lambda x: x[4] == occupation, db):
        print("{0} | {1} {2}".format(*match))
    print('-'*25)

def main():
    oper_help = """\
--------------------------
{title:^25}
--------------------------
get  | Get by ID
scan | Search by occupation
quit | Quit
--------------------------""".format(title="People Database")

    operations = {
        "get": get_id,
        "scan": scan,
        "quit": sys.exit,
    }

    while True:
        cls()
        print(oper_help)

        oper = input("Selection > ")

        if oper in operations:
            operations[oper]()
        else:
            sys.stderr.write("ERROR: No such command: '{}'\n".format(oper))

        input("Press ENTER to continue...")

if __name__ == '__main__':
    main()
